        boolean isEmancipated = scanner.next().equalsIgnoreCase("s");

        if(age >= 18){
            System.out.printf("%s tem %s anos, você pode dirigir. \n", name, age);
        } else if (age >= 16 && isEmancipated) {
            System.out.printf("%s, apesar de você ter %s anos, você é emancipado e pode dirigir. \n", name, age);

        } else {
            System.out.printf("%s tem %s anos, você não pode dirigir. \n", name, age);

        }

        boolean canDrive = (age > 18) || (age >= 16 && isEmancipated);

        String message = canDrive ?
                name + ", você pode dirigir.\n" :
                name + ", você não pode dirigir \n";

        System.out.println(message);

        System.out.println("Fim de execução");
    }

}

package dio.curso;

import java.util.Scanner;

public class MainKeywords {

    public static void main(String[] args){

        float number = 1.0f;
        var number1 = 1.0;

        char character = 'a';
        System.out.println("number ->"+ number);
        System.out.println("number 1 ->"+ number1);
        System.out.println("char ->"+ character);

        // operadores em java

        var scanner = new Scanner(System.in);

        System.out.println("Quanto é 2 = 2 ?");

        var result = scanner.nextInt();
        var isRight = result == 4;


        System.out.printf("O resultado é 4, você acertou? (%s) \n", isRight);
        if(result == 4){
            System.out.println("Você acertou!");
        } else {
            System.out.println("Você errou!");
        }


        System.out.println("Quantos anos você tem?");
        var age = scanner.nextInt();

        System.out.println("Você é emancipado?");
        var isEmancipated = scanner.nextBoolean();

        var canDrive = age >= 17 || (isEmancipated && age >= 16);

        System.out.printf("Você pode digigir? %s \n", canDrive);


    }

}

package dio.curso;

import java.util.Scanner;

public class MainKeywords {

    public static void main(String[] args){

        float number = 1.0f;
        double number1 = 1.0;

        char character = 'a';
        System.out.println("number ->"+ number);
        System.out.println("number 1 ->"+ number1);
        System.out.println("char ->"+ character);

        // operadores em java

        Scanner scanner = new Scanner(System.in);

        System.out.println("Quanto é 2 = 2 ?");

        int result = scanner.nextInt();
        boolean isRight = result == 4;


        System.out.printf("O resultado é 4, você acertou? (%s) \n", isRight);
        if(result == 4){
            System.out.println("Você acertou!");
        } else {
            System.out.println("Você errou!");
        }


        System.out.println("Quantos anos você tem?");
        int age = scanner.nextInt();

        System.out.println("Você é emancipado?");
        boolean isEmancipated = scanner.nextBoolean();

        boolean canDrive = age >= 17 || (isEmancipated && age >= 16);

        System.out.printf("Você pode digigir? %s \n", canDrive);


    }

}

package dio.curso;

import com.formdev.flatlaf.FlatLightLaf;
import net.miginfocom.swing.MigLayout;

import javax.swing.*;
import java.awt.*;
import java.awt.event.ActionEvent;
import java.awt.event.ActionListener;

public class MainTela extends JFrame {
    private JComboBox<Integer> comboBox;
    private JLabel resultadoLabel;
//    FlatLightLaf.setup();

    public MainTela() {
        dio.curso.FlatLightLaf.setup(); // Aplica tema moderno
        setTitle("Dias da Semana");
        setSize(350, 180);
        setDefaultCloseOperation(EXIT_ON_CLOSE);
        setLocationRelativeTo(null);
        setLayout(new MigLayout("", "[][grow]", "[][]20[]"));

        JLabel instrucao = new JLabel("Escolha um número de 1 a 7:");
        instrucao.setFont(instrucao.getFont().deriveFont(Font.BOLD, 14f));
        comboBox = new JComboBox<>();
        for (int i = 1; i <= 7; i++) {
            comboBox.addItem(i);
        }
        comboBox.setFont(comboBox.getFont().deriveFont(13f));

        JButton botao = new JButton("Ver dia");
        botao.setFont(botao.getFont().deriveFont(Font.BOLD, 13f));
        resultadoLabel = new JLabel("");
        resultadoLabel.setFont(resultadoLabel.getFont().deriveFont(Font.BOLD, 15f));
        resultadoLabel.setForeground(new Color(33, 150, 243));

        botao.addActionListener(new ActionListener() {
            @Override
            public void actionPerformed(ActionEvent e) {
                int option = (int) comboBox.getSelectedItem();
                String dia;
                switch (option) {
                    case 1: dia = "Domingo"; break;
                    case 2: dia = "Segunda"; break;
                    case 3: dia = "Terça"; break;
                    case 4: dia = "Quarta"; break;
                    case 5: dia = "Quinta"; break;
                    case 6: dia = "Sexta"; break;
                    case 7: dia = "Sábado"; break;
                    default: dia = "Inválido";
                }
                resultadoLabel.setText(dia);
            }
        });

        add(instrucao, "span, wrap");
        add(new JLabel("Número:"));
        add(comboBox, "growx, wrap");
        add(botao, "span, center, wrap");
        add(resultadoLabel, "span, center");
    }

    public static void main(String[] args) {
        SwingUtilities.invokeLater(() -> {
            new MainTela().setVisible(true);
        });
    }
}

